/**
 * @description 日常任务
 */
import React from 'react';
import Back from '@/components/Back';
import Flower from '@/components/Flower';
import './index.less';

const Tasks = () => {
  const list = [
    {
      key: 'zheng',
      title: '政',
      taskName: '开源节流',
      taskNum: 0,
      harvestFalg: true,
    },
    {
      key: 'cai',
      title: '才',
      taskName: '助攻易物',
      taskNum: 0,
      harvestFalg: true,
    },
    {
      key: 'mei',
      title: '魅',
      taskName: '布施济民',
      taskNum: 0,
      harvestFalg: true,
    },
  ];
  return (
    <div className='tasks'>
      <Back />
      <div className='tasks-title'>内务打理</div>
      <div className='tasks-list'>
        {list.map(item => {
          return (
            <div className='tasks-item' key={item.key}>
              <div className='tasks-item-top'></div>
              <div className='tasks-item-title'>{item.title}</div>
              <div className='tasks-item-num'>{item.num}</div>
              <div className='tasks-item-btn' onClick={() => handleHarvest(item)}>
                <div className='btn-flower1'>
                  <Flower />
                </div>
                <div className='btn-flower2'>
                  <Flower />
                </div>
                <span>{item.harvestFalg ? '收获' : '恢复'}</span>
              </div>
            </div>
          );
        })}
      </div>
      <div className='tasks-footer'></div>
    </div>
  );
};

export default Tasks;
