/**
 * @description 公共方法
 */
import moment from 'moment';

// 获取用户信息
const getUserInfo = () => {
  let userInfo = localStorage.getItem('userInfo');
  if (userInfo) {
    return JSON.parse(userInfo);
  }
  return null;
};

// 保存用户信息
const saveUserInfo = userInfo => {
  if (userInfo) {
    localStorage.setItem('userInfo', JSON.stringify(userInfo));
  }
};

/**
 * 两个是否可以整除
 * @param {number} num1 除数
 * @param {number} num2 被除数
 * @return {boolean} 是否整除的布尔值
 */
const getNumDivisibleFlag = (num1, num2) => {
  let flag = false;
  // 如果除数小于被除数 则表示不可以被整除
  if (num1 > num2 && num1 / num2 > 1) {
    flag = true;
  }
  return flag;
};

/**
 * 获取倒计时
 * @param {number} num1 除数
 * @param {number} num2 被除数
 * @return {string} 字符串时间
 */
const getCountdown = endDate => {
  let nowDate = new Date();
  // 剩余时间 毫秒
  let surplusTime = endDate.getTime() - nowDate.getTime();
  if (surplusTime <= 0) {
    return '0';
  } else {
    // 剩余时间 秒
    let lsurplusSecond = parseInt(surplusTime / 1000);
    let day = Math.floor(lsurplusSecond / (60 * 60 * 24));
    let hour = Math.floor((lsurplusSecond - day * 24 * 60 * 60) / 3600);
    let minute = Math.floor((lsurplusSecond - day * 24 * 60 * 60 - hour * 3600) / 60);
    let second = Math.floor((lsurplusSecond / 1000) % 60, 10);
    return `${day}天 ${hour}:${minute}:${second}`;
  }
};

export default { getUserInfo, saveUserInfo, getNumDivisibleFlag, getCountdown };
