/**
 * @description 公共方法
 */
// 获取用户信息
const getUserInfo = () => {
  let userInfo = localStorage.getItem('userInfo');
  if (userInfo) {
    return JSON.parse(userInfo);
  }
  return null;
};

// 保存用户信息
const saveUserInfo = userInfo => {
  if (userInfo) {
    localStorage.setItem('userInfo', JSON.stringify(userInfo));
  }
};

/**
 * 两个是否可以整除
 * @param {number} num1 除数
 * @param {number} num2 被除数
 * @return {boolean} 是否整除的布尔值
 */
const getNumDivisibleFlag = (num1, num2) => {
  let flag = false;
  // 如果除数小于被除数 则表示不可以被整除
  if (num1 > num2 && num1 / num2 > 1) {
    flag = true;
  }
  return flag;
};

/**
 * 获取倒计时
 * @param {number} num1 除数
 * @param {number} num2 被除数
 * @return {string} 字符串时间
 */
const getCountdown = () => {
  let now = new Date();
  let endDate = new Date(year, month - 1, day, hours);
  let leftTime = endDate.getTime() - now.getTime(); //计算剩余的毫秒数
  if (leftTime <= 0) {
    leftTime = 0;
  }
  let leftsecond = parseInt(leftTime / 1000); //计算剩余的秒数
  day = Math.floor(leftsecond / (60 * 60 * 24));
  let hour = Math.floor((leftsecond - day * 24 * 60 * 60) / 3600);
  let minute = Math.floor((leftsecond - day * 24 * 60 * 60 - hour * 3600) / 60);
  let second = Math.floor((leftTime / 1000) % 60, 10);
  return `${day}天 ${hour}:${minute}:${second}`;
};

export default { getUserInfo, saveUserInfo, getNumDivisibleFlag };
