/**
 * @description 头像组件
 */
import React from 'react';
import './index.less';
const Avatar = () => {
  return (
    <div className='avatar'>
      <img className='avatar-img' src='https://p6-passport.byteacctimg.com/img/user-avatar/c6c1a335a3b48adc43e011dd21bfdc60~300x300.image' alt='' />
      <div className='avatar-nickname'>叶一一</div>
      <div className='avatar-designation'>
        <span>淑仪倾城</span>
        <div className='avatar-line avatar-line1'></div>
        <div className='avatar-line avatar-line2'></div>
        <div className='avatar-line avatar-line3'></div>
        <div className='avatar-line avatar-line4'></div>
      </div>
    </div>
  );
};

export default Avatar;
